CREATE DEFINER=`perigrammata_db`@`localhost` PROCEDURE `addCourse`(IN `school` INT, IN `department` INT, IN `LevelOfEducation` INT, IN `l_code` VARCHAR(45) CHARSET utf8, IN `Sem` INT, IN `title` VARCHAR(200) CHARSET utf8, IN `Lectures` FLOAT, IN `Laboratories` FLOAT, IN `Tutorials` FLOAT, IN `LabTutorials` FLOAT, IN `Total` FLOAT, IN `Credit` INT, IN `lang` INT, IN `Erasmus` INT, IN `CourseContent` TEXT CHARSET utf8, OUT `lastId` INT)
