CREATE DEFINER=`perigrammata_db`@`localhost` PROCEDURE `checkSkill`(IN `cId` INT)
    NO SQL
SELECT course_has_skills.CourseId,course_has_skills.SkillsId,skills.Description,activity_has_skills.ActivityId,teaching_organization.Hours as hours
FROM activity_has_skills, course_has_skills ,teaching_organization,skills
WHERE activity_has_skills.SkillId = course_has_skills.SkillsId and course_has_skills.CourseId = cId and teaching_organization.ActivityId = activity_has_skills.ActivityId and teaching_organization.CourseId=course_has_skills.CourseId and skills.Id = activity_has_skills.SkillId
ORDER BY Hours DESC ;;
DELIMITER ;
