SELECT CourseId,round(Criterion1*100) as c1,round(Criterion2*100) as c2, round(Criterion3*100) as c3,round(Criterion4*100) as c4,round(Criterion5*100) as c5,round(Criterion6*100) as c6,round(Criterion7*100) as c7 FROM `abet_outcomes` WHERE CourseId = cId and OutcomeId = 99;
