CREATE DEFINER=`perigrammata_db`@`localhost` PROCEDURE `getAbetScoreBySchool`(IN `dId` INT)
    NO SQL
select (sums.c1/total.t)*100 as c1,(sums.c2/total.t)*100 as c2,(sums.c3/total.t)*100 as c3 ,(sums.c4/total.t)*100 as c4 ,(sums.c5/total.t)*100 as c5 ,(sums.c6/total.t)*100 as c6 ,(sums.c7/total.t)*100 as c7
FROM
(select COUNT(*) AS t from abet_outcomes ,courses where courses.Id = abet_outcomes.CourseId and OutcomeId = 99 and (Criterion1<>0 or Criterion2<>0 or Criterion3<>0 OR Criterion4<>0 or Criterion5<>0 OR Criterion6<>0 OR Criterion7<>0) and courses.DepartmentId =dId) as total,
(select SUM(Criterion1) as c1, SUM(Criterion2) as c2, SUM(Criterion3) as c3, SUM(Criterion4) as c4, SUM(Criterion5) as c5, SUM(Criterion6) as c6, SUM(Criterion7) as c7  from abet_outcomes ,courses where courses.Id = abet_outcomes.CourseId and OutcomeId = 99 and (Criterion1<>0 or Criterion2<>0 or Criterion3<>0 OR Criterion4<>0 or Criterion5<>0 OR Criterion6<>0 OR Criterion7<>0) and courses.DepartmentId =dId) as sums ;;
DELIMITER ;
