CREATE DEFINER=`perigrammata_db`@`localhost` PROCEDURE `getActiveCoursesList`()
    NO SQL
SELECT DISTINCT (courses.CourseTitle),courses.LessonCode,courses.Semester,courses.Id ,school.SchoolName,courses.locked

