FROM

(SELECT count(DISTINCT(course_has_verbs.CourseId)) as activeCourses

                FROM course_has_verbs

                INNER JOIN verbs ON verbs.Id = course_has_verbs.VerbId)as y , (SELECT count(Id) as allCourses FROM `courses`  WHERE 1) as x ;;
