CREATE DEFINER=`perigrammata_db`@`localhost` PROCEDURE `getCourse`(IN `CourseId` INT)
BEGIN
	SELECT courses.*, school.SchoolName, department.DepartmentName, 
	   levelofeducation.Education,language_of_teaching.LanguageOfTeaching,
       course_type.CourseType, lecture_method.LectureMethod

	FROM courses
	INNER JOIN school ON school.Id = courses.SchoolId
	INNER JOIN department ON department.Id = courses.DepartmentId
	INNER JOIN levelofeducation ON levelofeducation.Id = courses.EducationId
	INNER JOIN language_of_teaching ON language_of_teaching.Id = courses.LangId
	LEFT JOIN course_type ON course_type.Id = courses.CourseTypeId
    LEFT JOIN lecture_method ON lecture_method.Id = courses.LectureMethodId
	WHERE courses.Id = CourseId;
END ;;
