CREATE DEFINER=`perigrammata_db`@`localhost` PROCEDURE `getCourseAssessment`(IN `CId` INT)
BEGIN
	SELECT course_has_category.*, bonus.Choices, assessment_categories.CategoryName, 
		   assessment_subcategories.SubcategoryName
	FROM course_has_category
    LEFT JOIN bonus ON bonus.Id = course_has_category.BonusId
	LEFT JOIN assessment_categories ON assessment_categories.Id = course_has_category.CategoryId
    LEFT JOIN assessment_subcategories ON assessment_subcategories.Id = course_has_category.SubcategoryId1 || assessment_subcategories.Id = course_has_category.SubcategoryId2 || assessment_subcategories.Id = course_has_category.SubcategoryId3 ||assessment_subcategories.Id = course_has_category.SubcategoryId4
    WHERE course_has_category.CourseId = CId
    ORDER BY method_number ASC;
END ;;
