DELIMITER ;;
CREATE DEFINER=`perigrammata_db`@`localhost` PROCEDURE `getDocumentedCoursePercent`(IN `cId` INT)
    NO SQL
BEGIN


select * , cast((currentOutcomes/totalOutcomes)*100 as decimal(10,0))as documentedCoursesPercent , doc.documented 
from
(SELECT COUNT(CourseId) as documented FROM `abet_outcomes` WHERE CourseId=cId and Criterion1<>0 and Criterion2<>0 and Criterion3<>0 and Criterion4<>0 and Criterion5<>0 and Criterion6<>0 and Criterion7<>0) as doc,
(SELECT DISTINCT (courses.CourseTitle),courses.LessonCode,courses.Id ,school.SchoolName
FROM course_has_verbs,verbs,courses,school,abet_outcomes
where verbs.Id = course_has_verbs.VerbId &&  course_has_verbs.CourseId=courses.Id && courses.SchoolId=school.Id && abet_outcomes.CourseId=course_has_verbs.CourseId) as n1,
(SELECT x.totalOutcomes,y.currentOutcomes,y.CourseId
    FROM (select COUNT(course_has_verbs.OrderNumber) as totalOutcomes,course_has_verbs.CourseId
    from course_has_verbs
    GROUP BY course_has_verbs.CourseId) as x,(SELECT count(abet_outcomes.OutcomeId) as currentOutcomes,abet_outcomes.CourseId
    FROM abet_outcomes
    WHERE abet_outcomes.Outcome NOT LIKE '%\_%' 
    GROUP BY abet_outcomes.CourseId) as y 
    WHERE x.CourseId=y.CourseId )as n2
where n1.Id=n2.CourseId AND n1.Id=cId
ORDER BY n1.SchoolName ASC;

END ;;
DELIMITER ;
