CREATE DEFINER=`perigrammata_db`@`localhost` PROCEDURE `getDocumentedCoursesPercent`()
    NO SQL
SELECT cast((allCourses/activeCourses)*100 as decimal(10,2))as activeCoursesPercent

FROM

(SELECT count(DISTINCT(course_has_verbs.CourseId)) as activeCourses

                FROM course_has_verbs

                INNER JOIN verbs ON verbs.Id = course_has_verbs.VerbId)as y ,  (SELECT count(DISTINCT(CourseId)) as allCourses FROM abet_outcomes   WHERE 1) as x ;;
DELIMITER ;
