CREATE DEFINER=`perigrammata_db`@`localhost` PROCEDURE `getEnglishVerb`(IN `cId` INT)
    NO SQL
select m.CourseId ,m.OrderNumber,verbs.Id, verbs.Verbs as enVerbs , m.Sentence ,m.Verbs as elVerbs
FROM verbs,
(select x.*,greek_to_english_verbs.englishId
FROM
    (SELECT course_has_verbs.*, verbs.Verbs
	FROM course_has_verbs
	INNER JOIN verbs ON verbs.Id = course_has_verbs.VerbId
	WHERE course_has_verbs.CourseId = cId)as x,greek_to_english_verbs
    WHERE x.VerbId=greek_to_english_verbs.greekId) as m
    where verbs.Id=m.englishId
  ORDER BY m.OrderNumber ;;
DELIMITER ;
