CREATE DEFINER=`perigrammata_db`@`localhost` PROCEDURE `UpdateCourse`(IN `school` INT, IN `department` INT, IN `LevelOfEducation` INT, IN `lCode` VARCHAR(45) CHARSET utf8, IN `Sem` INT, IN `title` VARCHAR(200) CHARSET utf8, IN `Lectures` FLOAT, IN `Laboratories` FLOAT, IN `Tutorials` FLOAT, IN `LabTutorial` FLOAT, IN `Total` FLOAT, IN `Credit` INT, IN `Erasmus` INT, IN `CourseContent` TEXT CHARSET utf8, IN `CourseId` INT)
BEGIN
	UPDATE perigrammata_db.courses
	SET courses.SchoolId = school, 
    courses.DepartmentId = department, 
    courses.EducationId = LevelOfEducation,
    courses.LessonCode = lCode, 
    courses.Semester = Sem, 
    courses.CourseTitle = title,
    courses.LectureHours = Lectures, 
    courses.LaboratoryHours = Laboratories, 
    courses.TutorialHours = Tutorials, 
	courses.LabTutorialHours = LabTutorial,
    courses.TotalHours = Total, 
    courses.CreditUnits = Credit, 
    courses.ErasmusFl = Erasmus,
    courses.Content = CourseContent
    WHERE courses.Id = CourseId;
END ;;
DELIMITER ;
